&lt;p&gt;&lt;figure class=&#34;highlight python&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# fresh state&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;state = proj.factory.entry_state()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;state.solver.add(x - y &amp;gt;= &lt;span class=&#34;number&#34;&gt;4&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;state.solver.add(y &amp;gt; &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;state.solver.&lt;span class=&#34;built_in&#34;&gt;eval&lt;/span&gt;(x)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;number&#34;&gt;5&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;state.solver.&lt;span class=&#34;built_in&#34;&gt;eval&lt;/span&gt;(y)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;state.solver.&lt;span class=&#34;built_in&#34;&gt;eval&lt;/span&gt;(x + y)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;number&#34;&gt;6&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;/p&gt;
&lt;h4 id=&#34;floating-point-numbers&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#floating-point-numbers&#34;&gt;#&lt;/a&gt; Floating point numbers&lt;/h4&gt;
&lt;p&gt;&lt;figure class=&#34;highlight python&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;12&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;13&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;14&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;15&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;16&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;17&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;18&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# fresh state&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;state = proj.factory.entry_state()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;a = state.solver.FPV(&lt;span class=&#34;number&#34;&gt;3.2&lt;/span&gt;, state.solver.fp.FSORT_DOUBLE)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;a&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;lt;FP64 FPV(&lt;span class=&#34;number&#34;&gt;3.2&lt;/span&gt;, DOUBLE)&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;b = state.solver.FPS(&lt;span class=&#34;string&#34;&gt;&amp;#x27;b&amp;#x27;&lt;/span&gt;, state.solver.fp.FSORT_DOUBLE)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;b&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;lt;FP64 FPS(&lt;span class=&#34;string&#34;&gt;&amp;#x27;FP_b_0_64&amp;#x27;&lt;/span&gt;, DOUBLE)&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;a + b&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;lt;FP64 fpAdd(&lt;span class=&#34;string&#34;&gt;&amp;#x27;RNE&amp;#x27;&lt;/span&gt;, FPV(&lt;span class=&#34;number&#34;&gt;3.2&lt;/span&gt;, DOUBLE), FPS(&lt;span class=&#34;string&#34;&gt;&amp;#x27;FP_b_0_64&amp;#x27;&lt;/span&gt;, DOUBLE))&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;a + &lt;span class=&#34;number&#34;&gt;4.4&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;lt;FP64 FPV(&lt;span class=&#34;number&#34;&gt;7.6000000000000005&lt;/span&gt;, DOUBLE)&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;b + &lt;span class=&#34;number&#34;&gt;2&lt;/span&gt; &amp;lt; &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;lt;Bool fpLT(fpAdd(&lt;span class=&#34;string&#34;&gt;&amp;#x27;RNE&amp;#x27;&lt;/span&gt;, FPS(&lt;span class=&#34;string&#34;&gt;&amp;#x27;FP_b_0_64&amp;#x27;&lt;/span&gt;, DOUBLE), FPV(&lt;span class=&#34;number&#34;&gt;2.0&lt;/span&gt;, DOUBLE)), FPV(&lt;span class=&#34;number&#34;&gt;0.0&lt;/span&gt;, DOUBLE))&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;/p&gt;
&lt;p&gt;This is nice, but sometimes we need to be able to work directly with the representation of the float as a bitvector. You can interpret bitvectors as floats and vice versa, with the methods  &lt;code&gt;raw_to_bv&lt;/code&gt;  and  &lt;code&gt;raw_to_fp&lt;/code&gt; :&lt;/p&gt;
&lt;p&gt;&lt;figure class=&#34;highlight python&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;a.raw_to_bv()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;lt;BV64 &lt;span class=&#34;number&#34;&gt;0x400999999999999a&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;b.raw_to_bv()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;lt;BV64 fpToIEEEBV(FPS(&lt;span class=&#34;string&#34;&gt;&amp;#x27;FP_b_0_64&amp;#x27;&lt;/span&gt;, DOUBLE))&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;state.solver.BVV(&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;64&lt;/span&gt;).raw_to_fp()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;lt;FP64 FPV(&lt;span class=&#34;number&#34;&gt;0.0&lt;/span&gt;, DOUBLE)&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;state.solver.BVS(&lt;span class=&#34;string&#34;&gt;&amp;#x27;x&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;64&lt;/span&gt;).raw_to_fp()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;lt;FP64 fpToFP(x_1_64, DOUBLE)&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;/p&gt;
&lt;p&gt;&lt;figure class=&#34;highlight python&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;a&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;lt;FP64 FPV(&lt;span class=&#34;number&#34;&gt;3.2&lt;/span&gt;, DOUBLE)&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;a.val_to_bv(&lt;span class=&#34;number&#34;&gt;12&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;lt;BV12 &lt;span class=&#34;number&#34;&gt;0x3&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;a.val_to_bv(&lt;span class=&#34;number&#34;&gt;12&lt;/span&gt;).val_to_fp(state.solver.fp.FSORT_FLOAT)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;lt;FP32 FPV(&lt;span class=&#34;number&#34;&gt;3.0&lt;/span&gt;, FLOAT)&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;/p&gt;
&lt;h4 id=&#34;more-solving-methods&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#more-solving-methods&#34;&gt;#&lt;/a&gt; More Solving Methods&lt;/h4&gt;
&lt;p&gt;&lt;code&gt;eval&lt;/code&gt;  will give you one possible solution to an expression, but what if you want several? What if you want to ensure that the solution is unique? The solver provides you with several methods for common solving patterns:&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;&lt;code&gt;solver.eval(expression)&lt;/code&gt;  will give you one possible solution to the given expression.&lt;/li&gt;
&lt;li&gt;&lt;code&gt;solver.eval_one(expression)&lt;/code&gt;  will give you the solution to the given expression, or throw an error if more than one solution is possible.&lt;/li&gt;
&lt;li&gt;&lt;code&gt;solver.eval_upto(expression, n)&lt;/code&gt;  will give you up to n solutions to the given expression, returning fewer than n if fewer than n are possible.&lt;/li&gt;
&lt;li&gt;&lt;code&gt;solver.eval_atleast(expression, n)&lt;/code&gt;  will give you n solutions to the given expression, throwing an error if fewer than n are possible.&lt;/li&gt;
&lt;li&gt;&lt;code&gt;solver.eval_exact(expression, n)&lt;/code&gt;  will give you n solutions to the given expression, throwing an error if fewer or more than are possible.&lt;/li&gt;
&lt;li&gt;&lt;code&gt;solver.min(expression)&lt;/code&gt;  will give you the minimum possible solution to the given expression.&lt;/li&gt;
&lt;li&gt;&lt;code&gt;solver.max(expression)&lt;/code&gt;  will give you the maximum possible solution to the given expression.&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;Additionally, all of these methods can take the following keyword arguments:&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;&lt;code&gt;extra_constraints&lt;/code&gt;  can be passed as a tuple of constraints. These constraints will be taken into account for this evaluation, but will not be added to the state.&lt;/li&gt;
&lt;li&gt;&lt;code&gt;cast_to&lt;/code&gt;  can be passed a data type to cast the result to. Currently, this can only be  &lt;code&gt;int&lt;/code&gt;  and  &lt;code&gt;bytes&lt;/code&gt; , which will cause the method to return the corresponding representation of the underlying data. For example,  &lt;code&gt;state.solver.eval(state.solver.BVV(0x41424344, 32), cast_to=bytes)&lt;/code&gt;  will return  &lt;code&gt;b&#39;ABCD&#39;&lt;/code&gt; .&lt;/li&gt;
&lt;/ul&gt;
&lt;h3 id=&#34;machine-state-memory-registers-and-so-on&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#machine-state-memory-registers-and-so-on&#34;&gt;#&lt;/a&gt; Machine State - memory, registers, and so on&lt;/h3&gt;
&lt;p&gt;quick examples:&lt;/p&gt;
&lt;p&gt;&lt;figure class=&#34;highlight python&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;12&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;13&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;14&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;15&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;&lt;span class=&#34;keyword&#34;&gt;import&lt;/span&gt; angr, claripy&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;proj = angr.Project(&lt;span class=&#34;string&#34;&gt;&amp;#x27;/bin/true&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;state = proj.factory.entry_state()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# copy rsp to rbp&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;state.regs.rbp = state.regs.rsp&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# store rdx to memory at 0x1000&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;state.mem[&lt;span class=&#34;number&#34;&gt;0x1000&lt;/span&gt;].uint64_t = state.regs.rdx&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# dereference rbp&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;state.regs.rbp = state.mem[state.regs.rbp].uint64_t.resolved&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# add rax, qword ptr [rsp + 8]&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;state.regs.rax += state.mem[state.regs.rsp + &lt;span class=&#34;number&#34;&gt;8&lt;/span&gt;].uint64_t.resolved&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;/p&gt;
&lt;p&gt;这里开始使用更简单的执行方法： &lt;code&gt;state.step()&lt;/code&gt; ，其会进行一步符号执行并且返回名为&lt;a href=&#34;https://docs.angr.io/en/latest/api.html#angr.engines.successors.SimSuccessors&#34;&gt; &lt;code&gt;angr.engines.successors.SimSuccessors&lt;/code&gt; &lt;/a&gt; 的对象，并且会提供若干可以被分类成不同执行路径的继承状态，关注该对象的  &lt;code&gt;.successors&lt;/code&gt;  属性，其是一个包含所有 “normal” successors of a given step 的 list。&lt;/p&gt;
&lt;p&gt;该 list 会包含所有 constraint 的正误状态作为新的 constraint&lt;/p&gt;
&lt;p&gt;(这里的 example 应该是用一个 strcmp 作为 constraint)&lt;/p&gt;
&lt;p&gt;&lt;figure class=&#34;highlight python&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;12&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;13&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;proj = angr.Project(&lt;span class=&#34;string&#34;&gt;&amp;#x27;examples/fauxware/fauxware&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;state = proj.factory.entry_state(stdin=angr.SimFile)  &lt;span class=&#34;comment&#34;&gt;# ignore that argument for now - we&amp;#x27;re disabling a more complicated default setup for the sake of education&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;&lt;span class=&#34;keyword&#34;&gt;while&lt;/span&gt; &lt;span class=&#34;literal&#34;&gt;True&lt;/span&gt;:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;... &lt;/span&gt;    succ = state.step()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;... &lt;/span&gt;    &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; &lt;span class=&#34;built_in&#34;&gt;len&lt;/span&gt;(succ.successors) == &lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;... &lt;/span&gt;        &lt;span class=&#34;keyword&#34;&gt;break&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;... &lt;/span&gt;    state = succ.successors[&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;state1, state2 = succ.successors&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;state1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;lt;SimState @ &lt;span class=&#34;number&#34;&gt;0x400629&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;state2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;lt;SimState @ &lt;span class=&#34;number&#34;&gt;0x400699&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;/p&gt;
&lt;p&gt;可以 use  &lt;code&gt;state.posix.stdin.load(0, state.posix.stdin.size)&lt;/code&gt;  to retrieve a bitvector representing all the content read from stdin so far&lt;/p&gt;
&lt;p&gt;&lt;figure class=&#34;highlight python&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;input_data = state1.posix.stdin.load(&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;, state1.posix.stdin.size)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;state1.solver.&lt;span class=&#34;built_in&#34;&gt;eval&lt;/span&gt;(input_data, cast_to=&lt;span class=&#34;built_in&#34;&gt;bytes&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;string&#34;&gt;b&amp;#x27;\x00\x00\x00\x00\x00\x00\x00\x00\x00SOSNEAKY\x00\x00\x00&amp;#x27;&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;state2.solver.&lt;span class=&#34;built_in&#34;&gt;eval&lt;/span&gt;(input_data, cast_to=&lt;span class=&#34;built_in&#34;&gt;bytes&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;string&#34;&gt;b&amp;#x27;\x00\x00\x00\x00\x00\x00\x00\x00\x00S\x00\x80N\x00\x00 \x00\x00\x00\x00&amp;#x27;&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;/p&gt;
&lt;h4 id=&#34;state-presets&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#state-presets&#34;&gt;#&lt;/a&gt; State Presets&lt;/h4&gt;
&lt;p&gt;&lt;code&gt;project.factory.&lt;/code&gt;&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;&lt;code&gt;.blank_state()&lt;/code&gt;  constructs a “blank slate” blank state, with most of its data left uninitialized. When accessing uninitialized data, an unconstrained symbolic value will be returned.&lt;/li&gt;
&lt;li&gt;&lt;code&gt;.entry_state()&lt;/code&gt;  constructs a state ready to execute at the main binary’s entry point.&lt;/li&gt;
&lt;li&gt;&lt;code&gt;.full_init_state()&lt;/code&gt;  constructs a state that is ready to execute through any initializers that need to be run before the main binary’s entry point, for example, shared library constructors or preinitializers. When it is finished with these it will jump to the entry point.&lt;/li&gt;
&lt;li&gt;&lt;code&gt;.call_state()&lt;/code&gt;  constructs a state ready to execute a given function.&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;使用方法：&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;传入起始地址：&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;​	All of these constructors can take an  &lt;code&gt;addr&lt;/code&gt;  argument to specify the exact address to start.&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;传入参数：&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;​	If you’re executing in an environment that can take command line arguments or an environment, you can pass a list of arguments through  &lt;code&gt;args&lt;/code&gt;  and a dictionary of environment variables through  &lt;code&gt;env&lt;/code&gt;  into  &lt;code&gt;entry_state&lt;/code&gt;  and  &lt;code&gt;full_init_state&lt;/code&gt; . The values in these structures can be strings or bitvectors, and will be serialized into the state as the arguments and environment to the simulated execution. The default  &lt;code&gt;args&lt;/code&gt;  is an empty list, so if the program you’re analyzing expects to find at least an  &lt;code&gt;argv[0]&lt;/code&gt; , you should always provide that!&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;可以传入符号&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;​	If you’d like to have  &lt;code&gt;argc&lt;/code&gt;  be symbolic, you can pass a symbolic bitvector as  &lt;code&gt;argc&lt;/code&gt;  to the  &lt;code&gt;entry_state&lt;/code&gt;  and  &lt;code&gt;full_init_state&lt;/code&gt;  constructors. Be careful, though: if you do this, you should also add a constraint to the resulting state that your value for argc cannot be larger than the number of args you passed into  &lt;code&gt;args&lt;/code&gt; .&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;传入函数参数&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;​	To use the call state, you should call it with  &lt;code&gt;.call_state(addr, arg1, arg2, ...)&lt;/code&gt; , where  &lt;code&gt;addr&lt;/code&gt;  is the address of the function you want to call and  &lt;code&gt;argN&lt;/code&gt;  is the Nth argument to that function, either as a Python integer, string, or array, or a bitvector. If you want to have memory allocated and actually pass in a pointer to an object, you should wrap it in an PointerWrapper, i.e.  &lt;code&gt;angr.PointerWrapper(&amp;quot;point to me!&amp;quot;)&lt;/code&gt; . The results of this API can be a little unpredictable, but we’re working on it.&lt;/p&gt;
&lt;p&gt;​	To specify the calling convention used for a function with  &lt;code&gt;call_state&lt;/code&gt; , you can pass a &lt;a href=&#34;https://docs.angr.io/en/latest/api.html#angr.calling_conventions.SimCC&#34;&gt; &lt;code&gt;SimCC&lt;/code&gt; &lt;/a&gt; instance as the  &lt;code&gt;cc&lt;/code&gt;  argument.:raw-html-m2r: We try to pick a sane default, but for special cases you will need to help angr out.&lt;/p&gt;
&lt;h4 id=&#34;对内存操作&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#对内存操作&#34;&gt;#&lt;/a&gt; 对内存操作&lt;/h4&gt;
&lt;p&gt;对内存地址批量操作&lt;/p&gt;
&lt;p&gt;&lt;figure class=&#34;highlight python&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;s = proj.factory.blank_state()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;s.memory.store(&lt;span class=&#34;number&#34;&gt;0x4000&lt;/span&gt;, s.solver.BVV(&lt;span class=&#34;number&#34;&gt;0x0123456789abcdef0123456789abcdef&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;128&lt;/span&gt;))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;s.memory.load(&lt;span class=&#34;number&#34;&gt;0x4004&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;6&lt;/span&gt;) &lt;span class=&#34;comment&#34;&gt;# load-size is in bytes&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;lt;BV48 &lt;span class=&#34;number&#34;&gt;0x89abcdef0123&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;&lt;span class=&#34;keyword&#34;&gt;import&lt;/span&gt; archinfo&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;s.memory.load(&lt;span class=&#34;number&#34;&gt;0x4000&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;4&lt;/span&gt;, endness=archinfo.Endness.LE) &lt;span class=&#34;comment&#34;&gt;# 小端序存储&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;lt;BV32 &lt;span class=&#34;number&#34;&gt;0x67452301&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;/p&gt;
&lt;h4 id=&#34;对寄存器操作&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#对寄存器操作&#34;&gt;#&lt;/a&gt; 对寄存器操作&lt;/h4&gt;
