&lt;p&gt;&lt;figure class=&#34;highlight python&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;enc = [  &lt;span class=&#34;number&#34;&gt;0x05&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0x03&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0x0F&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0x09&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0x1E&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0x5B&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0x12&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0x5C&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0x09&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0x0F&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &lt;span class=&#34;number&#34;&gt;0x10&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0x43&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0x56&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0x4B&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0x17&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0x40&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0x56&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0x55&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0x4E&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0x5B&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &lt;s