&lt;h5 id=&#34;official-exp&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#official-exp&#34;&gt;#&lt;/a&gt; Official exp:&lt;/h5&gt;
&lt;pre&gt;&lt;code&gt;#include &amp;quot;iostream&amp;quot;
#include &amp;quot;stdio.h&amp;quot;
using namespace std;
int val[50]=&amp;#123;26, 59, 30, 19, 66, 85, 94, 8, 3, 44, 5, 1, 41, 82, 76, 1,    12,    81,    73,    32&amp;#125;,
w[50]=&amp;#123;71, 34, 82, 23, 1,88,12,57, 10, 68, 5, 33,    37,    69,    98,    24, 26,    83, 16, 26&amp;#125;;
int dp[21][301];
int main()
&amp;#123;
    int t=200;
    int m = 20;
    for(int i=1;i&amp;lt;=m;i++) 
        for(int j=t;j&amp;gt;=0;j--)  
    &amp;#123;
        if(j&amp;gt;=w[i])//容量可以放 
        &amp;#123;
            int tmp;
            dp[i][j]=max(dp[i-1][j-w[i]]+val[i],dp[i-1][j]);
        &amp;#125;  
        else
        &amp;#123;
            dp[i][j]=dp[i-1][j];
        &amp;#125;              
    &amp;#125;
    //dp[i][j] = max(dp[i-1][j-w[i]]+val[i],dp[i-1][j]);
    int x = 20;
    int y = 200;
    while(x!=0)
    &amp;#123;
        if(dp[x-1][y-w[x]]+val[x] &amp;gt; dp[x-1][y])        
        &amp;#123;
            printf(&amp;quot;%d,&amp;quot;,x); 
            y-=w[x];
        &amp;#125;
        x-=1;
    &amp;#125;
    return 0;
